import React, { useEffect, useState } from "react";
import axios from "axios";
export default function FormBuilder() {
  const [fieldLabel, setFieldLabel] = useState("");
  const [inputName, setInputName] = useState("");
  const [inputType, setInputType] = useState("text");
  const [formName, setFormName] = useState("");
  const [fields, setFields] = useState([]);
  const [inputs, setInputs] = useState([]);
  const onSave = () => {
    console.log(fields);
    axios
      .post("http://localhost:5000/forms/addForm", {
        formName: formName,
        fields: fields,
      })
      .then((res) => console.log(res));

    setFields([]);
    setFormName("");
  };
  // useEffect(() => {
  //   console.log(fieldLabel);
  // }, [fieldLabel]);
  // useEffect(() => {
  //   console.log(inputName);
  // }, [inputName]);
  // useEffect(() => {

  // }, [inputType]);

  const onSubmit = () => {
    axios
      .post("http://localhost:5000/forms/addField", {
        fieldLabel: fieldLabel,
        inputName: inputName,
        inputType: inputType,
      })
      .then((res) => setFormName("hala"));
    console.log(formName);
    //setFields(...fields, res));
    //console.log(fields);
  };

  return (
    <div className="FormBuilder">
      <form action="/FormBuilder" method="get">
        <label htmlFor="flabel"> field label:</label>
        <input
          type="text"
          id="flabel"
          name="flabel"
          value={fieldLabel}
          onChange={(text) => setFieldLabel(text.target.value)}
        />
        <br />
        <br />
        <label htmlFor="iname">input name:</label>
        <input
          type="text"
          id="iname"
          name="iname"
          value={inputName}
          onChange={(text) => setInputName(text.target.value)}
        />
        <br />
        <br />
        <select
          id="inputType"
          name="input type"
          value={inputType}
          onChange={(text) => setInputType(text.target.value)}
        >
          <option value="text">text</option>
          <option value="color">color</option>
          <option value="date">date</option>
          <option value="email">email</option>
          <option value="tel">tel</option>
          <option value="number">number</option>
        </select>
        {/* {inputType === "text" ? (
          <input
            type="text"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "color" ? (
          <input
            type="color"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "date" ? (
          <input
            type="date"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "email" ? (
          <input
            type="email"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "tel" ? (
          <input
            type="tel"
            id="phone"
            name="phone"
            pattern="[0-9]{10}"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "number" ? (
          <input
            type="number"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null} */}

        <input type="submit" value="Submit" onClick={onSubmit} />
        <input type="submit" value="Save" onClick={onSave} />
      </form>
    </div>
  );
}
