        <form onSubmit={handleSubmit}>
          {fields.map((field) => (
            <div>
              <label htmlFor="flabel"> {field.inputType}: </label>

              <input
                required
                type={field.inputType}
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
              <br></br>
            </div>
          ))}
          <Button
            variant="contained"
            color="primary"
            type="submit"
            size="small"
            startIcon={<SaveIcon />}
          >
            Save
          </Button>
        </form>
      </div>
    </Container>
  );
}
