import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function FormBuilder(props) {
  const [fieldLabel, setFieldLabel] = useState("");
  const [inputName, setInputName] = useState("");
  const [inputType, setInputType] = useState("text");
  const [formName, setFormName] = useState("");
  const [fields, setFields] = useState([]);
  const [inputs, setInputs] = useState([]);
  let history = useHistory();
  const onSave = () => {
    console.log(fields);
    axios
      .post("http://localhost:5000/forms/addForm", {
        formName: formName,
        fields: fields,
      })
      .then((res) => console.log(res));

    setFields([]);
    setFormName("");

    history.push("/");
    history.go();
  };
  // useEffect(() => {
  //   console.log(fieldLabel);
  // }, [fieldLabel]);
  // useEffect(() => {
  //   console.log(inputName);
  // }, [inputName]);
  // useEffect(() => {

  // }, [inputType]);

  const onSubmit = (e) => {
    console.log(fieldLabel);
    console.log(inputName);
    console.log(inputType);
    setFields([
      ...fields,
      { fieldLabel: fieldLabel, inputName: inputName, inputType: inputType },
    ]);
    e.preventDefault();
    //console.log(fields);
  };
  useEffect(() => {
    console.log("entered");
    console.log(fields);
    console.log(fields.length);
  }, [fields]);
  return (
    <div className="FormBuilder">
      <form action="/FormBuilder" method="get">
        <label htmlFor="flabel"> field label:</label>
        <input
          type="text"
          id="flabel"
          name="flabel"
          value={fieldLabel}
          onChange={(text) => setFieldLabel(text.target.value)}
        />
        <br />
        <br />
        <label htmlFor="iname">input name:</label>
        <input
          type="text"
          id="iname"
          name="iname"
          value={inputName}
          onChange={(text) => setInputName(text.target.value)}
        />
        <br />
        <br />
        <select
          id="inputType"
          name="input type"
          value={inputType}
          onChange={(text) => setInputType(text.target.value)}
        >
          <option value="text">text</option>
          <option value="color">color</option>
          <option value="date">date</option>
          <option value="email">email</option>
          <option value="tel">tel</option>
          <option value="number">number</option>
        </select>
        {/* {inputType === "text" ? (
          <input
            type="text"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "color" ? (
          <input
            type="color"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "date" ? (
          <input
            type="date"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "email" ? (
          <input
            type="email"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "tel" ? (
          <input
            type="tel"
            id="phone"
            name="phone"
            pattern="[0-9]{10}"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null}
        {inputType === "number" ? (
          <input
            type="number"
            onChange={(val) => setInputVal(val.target.value)}
          ></input>
        ) : null} */}

        <input type="submit" value="Submit" onClick={onSubmit} />
        <br></br>
        <label htmlFor="formname">form name:</label>
        <input
          type="text"
          id="formname"
          name="formname"
          value={formName}
          onChange={(text) => setFormName(text.target.value)}
        />
        <input type="submit" value="Save" onClick={onSave} />
      </form>

      {fields.map((field) => (
        <div>
          <br></br>
          <label for={field.inputName}>
            field label: {field.fieldLabel}, input name: {field.inputName},
            input type:{field.inputType}{" "}
          </label>
        </div>
      ))}
    </div>
  );
}
