import React, { useEffect, useState } from "react";
import axios from "axios";
import Container from "@material-ui/core/Container";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";
import { useHistory } from "react-router-dom";
import { getFormName } from "./actions/getFormName";

export default function FormSubmit(props) {
  const [inputName, setInputName] = useState("");
  const [fields, setFields] = useState([]);
  const [formName, setFormName] = useState("");
  const [inputVal, setInputVal] = useState({});
  let history = useHistory();

  //console.log(props.match.params.FormId);
  useEffect(() => {
    (async function getFormNameFunc() {
      const formNameRes = await getFormName(props.match.params.FormId);
      setFormName(formNameRes.data);
    })();
  });

  useEffect(() => {
    console.log(inputVal);
  }, [inputVal]);

  useEffect(() => {
    axios
      .get("/forms/getFormFields", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        console.log(res);
        setFields(res.data);
        for (let i = 0; i < fields.length; i++) {
          inputVal[fields[i]._id] = "";
        }

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  const handleSubmit = () => {
    axios
      .post("/forms/addFormInput", {
        inputName: inputName,
        inputVal: inputVal,
        id: props.match.params.FormId,
      })

      .then((res) => console.log(res));
    // Alert();
    history.push("/");
    history.go();
    //console.log(inputVal);
  };
  return (
    <Container fixed>
      <div>
        {formName}
        <br></br>

        <form onSubmit={handleSubmit}>
          {fields.map((field) => (
            <div>
              <label htmlFor="flabel"> {field.inputType}: </label>

              <input
                required
                type={field.inputType}
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
              <br></br>
            </div>
          ))}
          <Button
            variant="contained"
            color="primary"
            type="submit"
            size="small"
            startIcon={<SaveIcon />}
          >
            Save
          </Button>
        </form>
      </div>
    </Container>
  );
}
