import React, { useEffect, useState } from "react";
import axios from "axios";

export default function FormSubmit(props) {
  const [type, setType] = useState("");
  const [inputName, setInputName] = useState("");
  const [fields, setFields] = useState([]);
  const [formName, setFormName] = useState("");
  const [inputVal, setInputVal] = useState("");

  //console.log(props.match.params.FormId);
  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getForm", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        console.log(res);
        setFormName(res.formName);
        setFields(res.fields);

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  const handleSubmit = () => {
    axios
      .post("http://localhost:5000/forms/addFieldInput", {
        formName: inputName,
        formVal: inputVal,
        id: props.match.params.FormId,
      })
      .then((res) => console.log(res));
  };
  return (
    <div>
      <input
        type="text"
        onChange={(name) => setInputName(name.target.value)}
      ></input>
      <input
        type={type}
        onChange={(val) => setInputVal(val.target.value)}
      ></input>
      <input type="submit" value="Add Field" onClick={handleSubmit} />
    </div>
  );
}
