import React, { useEffect, useState } from "react";
import axios from "axios";

export default function FormSubmit(props) {
  const [type, setType] = useState("");
  const [inputName, setInputName] = useState("");
  const [fields, setFields] = useState([]);
  const [formName, setFormName] = useState("");
  const [inputVal, setInputVal] = useState("");

  //console.log(props.match.params.FormId);
  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getFormName", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        //console.log(res.data.fields);
        setFormName(res.data.formName);

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getFormFields", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        console.log(res.data.fields);
        setFields(res.data.fields);

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  const handleSubmit = () => {
    axios
      .post("http://localhost:5000/forms/addFieldInput", {
        formName: inputName,
        formVal: inputVal,
        id: props.match.params.FormId,
      })
      .then((res) => console.log(res));
  };
  return (
    <div>
      {formName}
      <br></br>
      {fields.map((field) => (
        <input
          type={field.inputType}
          name={field.inputName}
          value={inputVal}
          onChange={(name) => setInputVal(name.target.value)}
        ></input>
      ))}
    </div>
  );
}
