import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";

export default function FormBuilder(props) {
  const [fieldLabel, setFieldLabel] = useState("");
  const [inputName, setInputName] = useState("");
  const [inputType, setInputType] = useState("text");
  const [formName, setFormName] = useState("");
  const [fields, setFields] = useState([]);
  const [inputs, setInputs] = useState([]);
  let history = useHistory();

  const onSave = () => {
    console.log(fields);
    axios
      .post("http://localhost:5000/forms/addForm", {
        formName: formName,
        fields: fields,
      })
      .then((res) => console.log(res));

    setFields([]);
    setFormName("");

    history.push("/");
    history.go();
  };

  const onSubmit = (e) => {
    setFields([
      ...fields,
      { fieldLabel: fieldLabel, inputName: inputName, inputType: inputType },
    ]);
    e.preventDefault();
  };

  return (
    <Container fixed>
      <div className="FormBuilder">
        <form action="/FormBuilder" method="get">
          <br></br>
          <label htmlFor="flabel"> field label: </label>

          <input
            required
            type="text"
            id="flabel"
            label="Required"
            value={fieldLabel}
            onChange={(text) => setFieldLabel(text.target.value)}
          />
          <br />
          <br />
          <label htmlFor="iname">input name: </label>
          <input
            type="text"
            id="iname"
            name="iname"
            value={inputName}
            onChange={(text) => setInputName(text.target.value)}
          />
          <br />
          <br />

          <Select
            labelId="demo-simple-select-outlined-label"
            id="demo-simple-select-outlined"
            value={inputType}
            onChange={(text) => setInputType(text.target.value)}
            label="Type"
          >
            <MenuItem value=""></MenuItem>
            <MenuItem value="text">text</MenuItem>
            <MenuItem value="color">color</MenuItem>
            <MenuItem value="date">date</MenuItem>
            <MenuItem value="email">email</MenuItem>
            <MenuItem value="tel">tel</MenuItem>
            <MenuItem value="number">number</MenuItem>
          </Select>
          <br></br>
          <Button
            variant="contained"
            color="primary"
            size="small"
            onClick={onSubmit}
          >
            Add
          </Button>
          <br></br>
          <label htmlFor="formname">form name:</label>
          <input
            type="text"
            id="formname"
            name="formname"
            value={formName}
            onChange={(text) => setFormName(text.target.value)}
          />
          <br></br>
          <Button
            variant="contained"
            color="primary"
            size="small"
            startIcon={<SaveIcon />}
            onClick={onSave}
          >
            Save
          </Button>
        </form>

        {fields.map((field) => (
          <div>
            <br></br>
            <TextField
              label={field.fieldLabel}
              name={field.inputName}
              type={field.inputType}
            />
          </div>
        ))}
      </div>
    </Container>
  );
}
