import React, { useEffect, useState } from "react";
import axios from "axios";
import Container from "@material-ui/core/Container";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";
import Alert from "@material-ui/lab/Alert";

export default function FormSubmit(props) {
  const [inputName, setInputName] = useState("");
  const [fields, setFields] = useState([]);
  const [formName, setFormName] = useState("");
  const [inputVal, setInputVal] = useState({});

  //console.log(props.match.params.FormId);
  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getFormName", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        //console.log(res.data.fields);
        setFormName(res.data.formName);

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  useEffect(() => {
    console.log(inputVal);
  }, [inputVal]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getFormFields", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        console.log(res);
        setFields(res.data);
        for (let i = 0; i < fields.length; i++) {
          inputVal[fields[i]._id] = "";
        }

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  const Alert = () => {
    return (
      <Alert severity="success">This is a success alert â€” check it out!</Alert>
    );
  };

  const handleSubmit = () => {
    axios
      .post("http://localhost:5000/forms/addFormInput", {
        inputName: inputName,
        inputVal: inputVal,
        id: props.match.params.FormId,
      })

      .then((res) => console.log(res));
    // Alert();
    alert("Added");
    //console.log(inputVal);
  };
  return (
    <Container fixed>
      <div>
        {formName}
        <br></br>
        {fields.map((field) => (
          <div>
            <label htmlFor="flabel"> {field.inputType}: </label>\
            {field.inputType == "email" ? (
              <input
                type="email"
                name={field.inputName}
                id="email"
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
            ) : null}
            {field.inputType == "tel" ? (
              <input
                type="tel"
                name={field.inputName}
                id="tel"
                value={inputVal[field._id]}
                pattern="[0-9]{3}[0-9]{7}"
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
            ) : null}
            {field.inputType == "number" ? (
              <input
                type="number"
                id="quantity"
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
            ) : null}
            {field.inputType == "date" ? (
              <input
                type="date"
                id="date"
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
            ) : null}
            {field.inputType == "text" ? (
              <input
                type="text"
                id="text"
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
            ) : null}
            {field.inputType == "color" ? (
              <input
                type="color"
                id="color"
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
            ) : null}
            <input
              type={field.inputType}
              name={field.inputName}
              value={inputVal[field._id]}
              onChange={(name) => (inputVal[field._id] = name.target.value)}
            ></input>
            <br></br>
          </div>
        ))}

        <Button
          variant="contained"
          color="primary"
          size="small"
          startIcon={<SaveIcon />}
          onClick={handleSubmit}
        >
          Save
        </Button>
      </div>
    </Container>
  );
}
