import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";

export default function FormBuilder(props) {
  const [fieldLabel, setFieldLabel] = useState("");
  const [inputName, setInputName] = useState("");
  const [inputType, setInputType] = useState("text");
  const [formName, setFormName] = useState("");
  const [fields, setFields] = useState([]);
  const [inputs, setInputs] = useState([]);
  let history = useHistory();

  const onSave = () => {
    console.log(fields);
    axios
      .post("http://localhost:5000/forms/addForm", {
        formName: formName,
        fields: fields,
      })
      .then((res) => console.log(res));

    setFields([]);
    setFormName("");

    history.push("/");
    history.go();
  };

  const onSubmit = (e) => {
    setFields([
      ...fields,
      { fieldLabel: fieldLabel, inputName: inputName, inputType: inputType },
    ]);
    e.preventDefault();
  };

  return (
    <div className="FormBuilder">
      <form action="/FormBuilder" method="get">
        <label htmlFor="flabel"> field label:</label>

        <TextField
          required
          id="outlined-required"
          label="Required"
          value={fieldLabel}
          variant="outlined"
          onChange={(text) => setFieldLabel(text.target.value)}
        />
        <br />
        <br />
        <label htmlFor="iname">input name:</label>
        <input
          type="text"
          id="iname"
          name="iname"
          value={inputName}
          onChange={(text) => setInputName(text.target.value)}
        />
        <br />
        <br />
        <select
          id="inputType"
          name="input type"
          value={inputType}
          onChange={(text) => setInputType(text.target.value)}
        >
          <option value="text">text</option>
          <option value="color">color</option>
          <option value="date">date</option>
          <option value="email">email</option>
          <option value="tel">tel</option>
          <option value="number">number</option>
        </select>

        <Button
          variant="contained"
          color="primary"
          size="small"
          onClick={onSubmit}
        >
          Add
        </Button>
        <br></br>
        <label htmlFor="formname">form name:</label>
        <input
          type="text"
          id="formname"
          name="formname"
          value={formName}
          onChange={(text) => setFormName(text.target.value)}
        />

        <Button
          variant="contained"
          color="primary"
          size="small"
          startIcon={<SaveIcon />}
          onClick={onSave}
        >
          Save
        </Button>
      </form>

      {fields.map((field) => (
        <div>
          <br></br>
          <TextField
            label={field.fieldLabel}
            name={field.inputName}
            type={field.inputType}
          />
        </div>
      ))}
    </div>
  );
}
