import React, { useEffect, useState } from "react";
import axios from "axios";
import Container from "@material-ui/core/Container";
import SaveIcon from "@material-ui/icons/Save";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { useTheme } from "@material-ui/core/styles";
export default function FormSubmit(props) {
  const [inputName, setInputName] = useState("");
  const [fields, setFields] = useState([]);
  const [formName, setFormName] = useState("");
  const [inputVal, setInputVal] = useState({});
  const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  //console.log(props.match.params.FormId);
  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getFormName", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        //console.log(res.data.fields);
        setFormName(res.data.formName);

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  useEffect(() => {
    console.log(inputVal);
  }, [inputVal]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/forms/getFormFields", {
        params: {
          id: props.match.params.FormId,
        },
      })
      .then((res) => {
        console.log(res);
        setFields(res.data);
        for (let i = 0; i < fields.length; i++) {
          inputVal[fields[i]._id] = "";
        }

        //setType(res.data.inputType);
      });
    //console.log(forms);

    //console.log(forms);
  }, []);

  const Alert = () => {
    return (
      <Alert severity="success">This is a success alert â€” check it out!</Alert>
    );
  };

  const handleSubmit = () => {
    axios
      .post("http://localhost:5000/forms/addFormInput", {
        inputName: inputName,
        inputVal: inputVal,
        id: props.match.params.FormId,
      })

      .then((res) => console.log(res));
    // Alert();

    return (
      <div>
        <Dialog
          fullScreen={fullScreen}
          open={open}
          onClose={handleClose}
          aria-labelledby="responsive-dialog-title"
        >
          <DialogTitle id="responsive-dialog-title">
            {"Use Google's location service?"}
          </DialogTitle>
          <DialogContent>
            <DialogContentText>added successfully!</DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button autoFocus onClick={handleClose} color="primary">
              Disagree
            </Button>
            <Button onClick={handleClose} color="primary" autoFocus>
              Agree
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );

    //console.log(inputVal);
  };
  return (
    <Container fixed>
      <div>
        {formName}
        <br></br>

        <form action="/FormSubmit" method="post">
          {fields.map((field) => (
            <div>
              <label htmlFor="flabel"> {field.inputType}: </label>

              <input
                type={field.inputType}
                name={field.inputName}
                value={inputVal[field._id]}
                onChange={(name) => (inputVal[field._id] = name.target.value)}
              ></input>
              <br></br>
            </div>
          ))}

          <Button
            variant="contained"
            color="primary"
            type="submit"
            size="small"
            startIcon={<SaveIcon />}
            onClick={handleSubmit}
          >
            Save
          </Button>
        </form>
      </div>
    </Container>
  );
}
